import React, {ChangeEvent, useState} from 'react';
import {Button} from './Button';
import '../App.css';

export const Counter = () => {
    const [count, setCount] = useState(0)
    const [showSettings, setShowSettings] = useState(false)
    const [startValue, setStartValue] = useState(0)
    const [maxValue, setMaxValue] = useState(0)

    const onClickAddCounter = () => setCount(count + 1)
    const onClickResetCounter = () => setCount(0)
    const onChangeStartInputHandler = (e: ChangeEvent<HTMLInputElement>) => setStartValue(+e.currentTarget.value)

    const onClickSettingsHandler = () => {
        setShowSettings(!showSettings)
        setCount(startValue)
    }

    return <div className={'main'}>
        <div className={'mainDisplay'}>
            {showSettings ? <>
                    <div>
                        <label htmlFor={'startValue'}>start value:</label>
                        <input type="number" name="startValue" onChange={onChangeStartInputHandler}/>
                    </div>
                    <div>
                        <label htmlFor={'maxValue'}>max value:</label>
                        <input type="number" name="maxValue"/>
                    </div>
                </> :
                <span>{count}</span>
            }
        </div>
        <div className={'buttons'}>
            {showSettings ?
                <Button title={'set'} btnClick={onClickSettingsHandler} disabled={false}/>
                :
                <>
                    <Button title={'inc'} btnClick={onClickAddCounter} disabled={false}/>
                    <Button title={'reset'} btnClick={onClickResetCounter} disabled={false}/>
                    <Button title={'set'} btnClick={onClickSettingsHandler} disabled={false}/>
                </>
            }
        </div>
    </div>
}
