import React from 'react';
import {Button} from './Button';
import '../App.css';

type CounterPropsType = {
    count: number
    setCount: (value: number) => void
    startValue: number
    maxValue: number
}

export const Counter = (props: CounterPropsType) => {

    const addCounter = () => {
        if (props.setCount && props.count < props.maxValue) {
            props.setCount(props.count + 1)
        }
    }

    const resetCounter = () => {
        if (props.setCount) {
            props.setCount(props.startValue)
        }
    }

    return (
        <div className={'main'}>
            <div className={'mainDisplay'}>
                <span className={props.count === props.maxValue ? 'redSpan' : ''}>{props.count}</span>
            </div>
            <div className={'buttons'}>
                <Button title={'Inc'} btnClick={addCounter} disabled={props.count === props.maxValue}/>
                <Button title={'Reset'} btnClick={resetCounter} disabled={false}/>
            </div>
        </div>
    );
};
