import React from 'react';
import Button from '../Button';
import Count from './Count';
import '../../App.css';

type CounterPropsType = {
    startValue: string
    setStartValue: (value: string) => void
}

export const Counter = (props: CounterPropsType) => {

    const addCounter = () => {
        // if (props.count < 5)
        props.setStartValue((props.startValue + 1).toString());
    }

    const resetCounter = () => props.setStartValue('0')

    return (
        <div className={'main'}>
            <div className={'mainDisplay'}>
                <Count count={props.startValue}/>
            </div>
            <div className={'buttons'}>
                <Button title={'Inc'} btnClick={addCounter} disabled={false}/>
                <Button title={'Reset'} btnClick={resetCounter} disabled={false}/>
            </div>
        </div>
    );
};
