import React, {ChangeEvent, useState} from 'react';
import {Button} from './Button';
import '../App.css';

export type CounterPropsType = {
    count: number
    setCount: (value: number) => void
    onUpdateSettings: () => void
}

export const SettingsCounter = (props: CounterPropsType) => {

    const [startValue, setStartValue] = useState<number>(0)
    const [maxValue, setMaxValue] = useState<number>(0)

    const changeInputMaxHandler = (e: ChangeEvent<HTMLInputElement>) => {
        if (setMaxValue) {
            setMaxValue(+e.currentTarget.value)
            props.onUpdateSettings(startValue, maxValue);
        }
    }

    const changeInputStartHandler = (e: ChangeEvent<HTMLInputElement>) => {
        if (setStartValue) {
            setStartValue(+e.currentTarget.value);
        }
    }

    const btnSetHandler = () => {
        if (props.setCount) {
            props.setCount(props.startValue)
        }
    }

    return (
        <div className={'main'}>
            <div className={'settings'}>
                <div>
                    <div>
                        <label>
                            max value:
                            <input
                                value={props.maxValue}
                                type={'number'}
                                onChange={changeInputMaxHandler}
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            start value:
                            <input
                                className={props.startValue < 0 ? 'inputMistake' : ''}
                                value={props.startValue}
                                type={'number'}
                                onChange={changeInputStartHandler}
                            />
                        </label>
                    </div>
                </div>
            </div>
            <div className={'buttons'}>
                <Button title={'Set'} btnClick={btnSetHandler} disabled={false}/>
            </div>
        </div>
    );
};
