export type InitialStateType = {
    count: number
    startValue: number
    maxValue: number
    showSettings: boolean
}

const initialState: InitialStateType = {
    count: 0,
    startValue: 0,
    maxValue: 5,
    showSettings: false
}

// type InitialStateType = typeof initialState;

export const counterReducer = (state: InitialStateType = initialState, action: ActionsTypes): InitialStateType => {
    const {count, startValue, maxValue, showSettings} = state
    switch (action.type) {
        case 'ADD-COUNTER': {
            return (count < maxValue) ? {...state, count: state.count + 1} : {...state}
        }
        case 'RESET-COUNTER': {
            return (startValue < 0) ? {...state, count: 0} : {...state, count: startValue}
        }
        default:
            return state
    }
}

type ActionsTypes = ReturnType<typeof addCounterAC> | ReturnType<typeof resetCounterAC>

export const addCounterAC = () => ({type: 'ADD-COUNTER'}) as const
export const resetCounterAC = () => ({type: 'RESET-COUNTER'}) as const
