import React, {ChangeEvent} from 'react';
import Button from './Button';
import '../App.css';

export type CounterPropsType = {
    startValue: number
    setStartValue: (value: number) => void
    count?: number
    setCount?: (value: number) => void
}

const SettingsCounter = ({startValue, setStartValue}: CounterPropsType) => {
    const changeInputMaxHandler = (e: ChangeEvent<HTMLInputElement>) => {
        // setMaxValue(e.currentTarget.value)
    }

    const changeInputStartHandler = (e: ChangeEvent<HTMLInputElement>) => {
        setStartValue(Number(e.currentTarget.value));
    }

    const btnSetHandler = () => {
        setStartValue(startValue)
    }

    return (
        <div className={'main'}>
            <div className={'settings'}>
                <div>
                    <div>
                        <label>
                            max value:
                            <input
                                // value={maxValue}
                                type={'number'}
                                onChange={changeInputMaxHandler}
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            start value:
                            <input
                                value={startValue}
                                type={'number'}
                                onChange={changeInputStartHandler}
                            />
                        </label>
                    </div>
                </div>
            </div>
            <div className={'buttons'}>
                <Button title={'Set'} btnClick={btnSetHandler} disabled={false}/>
            </div>
        </div>
    );
};

export default SettingsCounter;
